var Menubar = function (a) {
    var menubarPanel = new UI.Panel().setId("menubar");
  
    menubarPanel.add(new Menubar.File(a));
    menubarPanel.add(new Menubar.Edit(a));
    menubarPanel.add(new Menubar.Add(a));
    menubarPanel.add(new Menubar.Examples(a));
    menubarPanel.add(new Menubar.Help(a));
    menubarPanel.add(new Menubar.Status(a));
  
    return menubarPanel;
  };
  