Menubar.Help = function (title) {
    var menuPanel = new UI.Panel();
    menuPanel.setClass("menu");
  
    var titlePanel = new UI.Panel();
    titlePanel.setClass("title");
    titlePanel.setTextContent("Help");
    menuPanel.add(titlePanel);
  
    var optionsPanel = new UI.Panel();
    optionsPanel.setClass("options");
    menuPanel.add(optionsPanel);
  
    var aboutOption = new UI.Row();
    aboutOption.setClass("option");
    aboutOption.setTextContent(title);
    aboutOption.onClick(function () {
      window.open("https://kittytreeplanner.neocities.org/", "_blank");
    });
  
    optionsPanel.add(aboutOption);
  
    return menuPanel;
  };
  